
public class Terinary {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
	int x = 11;
		
		x = (11 == x) ? 1 : 0;   //  condition ?: True:False
		
		System.out.println(x);
		          x += 5;           // x = x + 5
				   x -= 5; // x = x - 5
				    x *= 5; // x = x * 5
				    x /= 4; // x = x / 4
				    x %= 5;   // x = x % 5
				System.out.println(x);

		
		
	}

}
